import React, { useEffect } from 'react';
import { useLocation } from "react-router-dom";

function ScrollTop(props) {
    
    const { pathname } = useLocation();

    useEffect(() => {
        window.scrollTo(0, 0);
      }, [pathname]);

    return <>{props.children}</>;

}

export default ScrollTop;
